        return new Source("{% extends 'events.html.twig' %}

{% block title %}Evénements{% endblock %}

{% block body %}

<div class=\"container-fluid my-4\">
    <h1>Tous nos événements</h1>
    <div class=\"row\">
        {% for evenement in allEvents %}
        <div class=\"card col-xs-12 col-sm-12 col-md-12 col-lg-4 col-xl-2 m-4\">
            <img src=\"...\" class=\"card-img-top\" alt=\"...\">
            <div class=\"card-body\">
            <h5 class=\"card-title\">{{ evenement.nom }}</h5>
            <p class=\"card-text\">{{ evenement.description }}</p>
            <p class=\"card-text\"><small class=\"text-muted\">Le {{ evenement.date|date(\"d M Y\")}} à {{ evenement.date|date(\"G\")}}h{{ evenement.date|date(\"i\")}}</small></p>
            </div>
        </div>
        {% endfor %}
    </div>
</div>

{% endblock %}", "publicPages/evenements/evenements_tous.html.twig", "C:\\Users\\Clément\\Documents\\CESI\\3eme_semestre\\BLOC_2\\ProjetWebBDE\\ProjetWeb\\ProjetWeb\\templates\\publicPages\\evenements\\evenements_tous.html.twig");
